import styled from "styled-components"

const IntroPage = () => {
  return (
    <Background>
      <Title>MOTI.</Title>
      <Downloads>
        <Download onClick={() => console.log("다운로드32")}>
          Download x32
        </Download>
        <Download onClick={() => console.log("다운로드64")}>
          Download x64
        </Download>
      </Downloads>
      <ImageChicken src="images/flowerchicken_playing.gif" />
      {/* <ImageEgg src="images/chickenegg_default.gif" /> */}
    </Background>
  )
}

export default IntroPage

const Background = styled.div`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  background: linear-gradient(#5a6078, #5a6099);
  // background: linear-gradient(black, grey);
  // background: linear-gradient(#d1a926, gold);
  // background: linear-gradient(#1f5f9c, #1f5f9c, #1f5f9c, #58a3e8);
`

const Title = styled.div`
  font-size: 250px;
  color: white;
  text-shadow: 5px 5px 5px grey;
`

const Downloads = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

const Download = styled.button`
  height: 100px;
  width: 400px;
  margin: 10px;
  background: skyblue;
  border-radius: 4px;
  color: white;
  font-size: 40px;
  text-shadow: 3px 3px 3px grey;
  box-shadow: 5px 5px 5px grey;
  border: solid white;
  display: flex;
  align-items: center;
  justify-content: center;
  &:hover {
    filter: brightness(80%);
  }
`

const ImageChicken = styled.img`
  height: 80px;
  width: 80px;
  position: absolute;
  right: 25%;
  top: 26%;
`

const ImageEgg = styled.img`
  height: 80px;
  width: 80px;
  position: absolute;
  left: 21%;
  top: 28%;
`
